fold_num = 10 kv_num = c(5, 10*c(1:5, 10)) seed_num = 2003 try_num = 1 smp&lt;-function(cross=fold_num,n,seed) { set.seed(seed) dd=list() aa0=sample(rep(1:cross,ceiling(n/cross))[1:n],n) for (i in 1:cross) dd[[i]]=(1:n)[aa0==i] return(dd) } selectK&lt;-function(dtm,kv=kv_num,SEED=seed_num,cross=fold_num,sp) # change 60 to 15 { per_ctm=NULL log_ctm=NULL for (k in kv) { per=NULL loglik=NULL for (i in 1:try_num) #only run for 3 replications# { cat(&quot;R is running for&quot;, &quot;topic&quot;, k, &quot;fold&quot;, i, as.character(as.POSIXlt(Sys.time(), &quot;Asia/Shanghai&quot;)),&quot;\n&quot;) te=sp[[i]] tr=setdiff(1:dtm$nrow, te) # setdiff(nrow(dtm),te) ## fix here when restart r session # VEM = LDA(dtm[tr, ], k = k, control = list(seed = SEED)), # VEM_fixed = LDA(dtm[tr,], k = k, control = list(estimate.alpha = FALSE, seed = SEED)), # CTM = CTM(dtm[tr,], k = k, # control = list(seed = SEED, var = list(tol = 10^-4), em = list(tol = 10^-3))) # Gibbs = LDA(dtm[tr,], k = k, method = &quot;Gibbs&quot;, control = list(seed = SEED, burnin = 1000,thin = 100, iter = 1000)) per=c(per,perplexity(Gibbs,newdata=dtm[te,])) loglik=c(loglik,logLik(Gibbs,newdata=dtm[te,])) } per_ctm=rbind(per_ctm,per) log_ctm=rbind(log_ctm,loglik) } return(list(perplex=per_ctm,loglik=log_ctm)) } sp=smp(n=dtm$nrow, seed=seed_num) # n = nrow(dtm) system.time((ctmK=selectK(dtm=dtm,kv=kv_num,SEED=seed_num,cross=fold_num,sp=sp))) ## plot the perplexity m_per=apply(ctmK[[1]],1,mean) m_log=apply(ctmK[[2]],1,mean) k=c(kv_num) df = ctmK[[1]] # perplexity matrix logLik = ctmK[[2]] # perplexity matrix write.csv(data.frame(k, df, logLik), paste(getwd(), &quot;/Perplexity2_&quot;,&quot;gibbs5_100&quot;, &quot;.csv&quot;, sep = &quot;&quot;)) # save the figure png(paste(getwd(), &quot;/Perplexity2_&quot;,try_num, &quot;_gibbs5_100&quot;,&quot;.png&quot;, sep = ''), width=5, height=5, units=&quot;in&quot;, res=700) matplot(k, df, type = c(&quot;b&quot;), xlab = &quot;Number of topics&quot;, ylab = &quot;Perplexity&quot;, pch=1:try_num,col = 1, main = '') legend(&quot;topright&quot;, legend = paste(&quot;fold&quot;, 1:try_num), col=1, pch=1:try_num) dev.off() png(paste(getwd(), &quot;/LogLikelihood2_&quot;, &quot;gibbs5_100&quot;,&quot;.png&quot;, sep = ''), width=5, height=5, units=&quot;in&quot;, res=700) matplot(k, logLik, type = c(&quot;b&quot;), xlab = &quot;Number of topics&quot;, ylab = &quot;Log-Likelihood&quot;, pch=1:try_num,col = 1, main = '') legend(&quot;topright&quot;, legend = paste(&quot;fold&quot;, 1:try_num), col=1, pch=1:try_num) dev.off()  {:lang=&laquo;ruby&raquo;}
