# Using cluster analysis to classify topics generated by topic modeling # 2013 Sep 08 # Cheng-Jun Wang library(tm) library(topicmodels) require(proxy) data(acq) data(crude) m &lt;- c(acq, crude) dtm &lt;- DocumentTermMatrix(m) dtm &lt;- removeSparseTerms(dtm, 0.8) inspect(dtm[1:5, 1:5]) # cluster analysis of documents based on DocumentTermMatrix dist_dtm &lt;- dissimilarity(mtd, method = 'cosine') hc &lt;- hclust(dist_dtm, method = 'ave') plot(hc, xlab = '')  {:lang=&laquo;ruby&raquo;}
