# topic modeling topic_num = 50 for (k in c(topic_num)) { # k &lt;- 10 SEED &lt;- 2010 jss_TM &lt;- list( VEM = LDA(dtm, k = k, control = list(seed = SEED)), VEM_fixed = LDA(dtm, k = k, control = list(estimate.alpha = FALSE, seed = SEED)), Gibbs = LDA(dtm, k = k, method = &quot;Gibbs&quot;, control = list(seed = SEED, burnin = 1000, thin = 100, iter = 1000)) ) } rs = posterior(jss_TM$Gibbs, dtm) mtd = t(rs$topics) # topics and documents mtt = rs$terms # topic and terms  {:lang=&laquo;ruby&raquo;}
