############################################ # # Hierarchical Clustering # ############################################ cos.sim &lt;- function(ix) { A = X[ix[1],] B = X[ix[2],] return( sum(A*B)/sqrt(sum(A^2)*sum(B^2)) ) } mdt = as.matrix(dtm) X = mtt # whether to scale it n &lt;- nrow(X) cmb &lt;- expand.grid(i=1:n, j=1:n) simdt &lt;- matrix(apply(cmb,1,cos.sim),n,n) rownames(simdt) = rownames(mtt) hc &lt;- hclust(dist(simdt, method = &quot;euclidean&quot;), method = 'ave') # hc &lt;- hclust(dist(simdt)^2, method = 'cen') plot(hc, xlab = '') k = 10 groups &lt;- cutree(hc, k=k) # cut tree into 5 clusters rect.hclust(hc, k=k, border=&quot;red&quot;) # draw dendogram with red borders  {:lang=&laquo;ruby&raquo;}
