library(openNLP) library(tm) require(NLP) # Compose the tagPOS function tagPOS &lt;- function(text.var, pos_tag_annotator, ...) { s &lt;- as.String(text.var) ## Set up the POS annotator if missing (for parallel) PTA &lt;- Maxent_POS_Tag_Annotator() ## Need sentence and word token annotations. word_token_annotator &lt;- Maxent_Word_Token_Annotator() a2 &lt;- Annotation(1L, &quot;sentence&quot;, 1L, nchar(s)) a2 &lt;- annotate(s, word_token_annotator, a2) a3 &lt;- annotate(s, PTA, a2) ## Determine the distribution of POS tags for word tokens. a3w &lt;- a3[a3$type == &quot;word&quot;] pos_tag &lt;- unlist(lapply(a3w$features, &quot;[[&quot;, &quot;POS&quot;)) ## Extract token/POS pairs (all of them): easy. pos_term &lt;- list(term = s[a3w], tag = pos_tag) return (pos_term) }  {:lang=&laquo;ruby&raquo;}
