# run tagPOS function for a list of texts # do so to facilitate the conversion to generate corpus for topic modeling run_pos = function(n){ cat(&quot;R is running for part-of-speech tagging&quot;, n, as.character(as.POSIXlt(Sys.time(), &quot;Asia/Shanghai&quot;)), sep = &quot;\n&quot;) df = tagPOS(text[n]) nn = (df$term[which(df$tag == &quot;NN&quot;)]) vb = (df$term[which(df$tag == &quot;VB&quot;)]) nnvb = (c(nn, vb)) result = list(nn, vb, nnvb) return(result) }  {:lang=&laquo;ruby&raquo;}
